import { useState } from "react";
import Select from "react-select";

export const SelectInput = ({ options, placeholder,setSelectedOption,selectedOptionClear }) => {
  const [selectedOption] = useState(null);
  return (
    <div style={{ width: 200 }}>
      <Select
        defaultValue={selectedOption}
        value={selectedOptionClear}
        onChange={setSelectedOption}
        options={options}
        isClearable={true}
        placeholder={placeholder}
      />
    </div>
  );
};
