import React, { useMemo } from "react";
import { useTable, usePagination } from "react-table";
import MOCK_DATA from "./MOCK_DATA.json";
import { COLUMNS } from "./columns";
import "./table.css";
import { SelectInput } from "./SelectInput";
import { useQuery } from "react-query";

export const FilteringTable = () => {

  const fatchTableData=()=>{
    axios.get("https://jsonplaceholder.typicode.com/users")
  }


  const {isLoading,data }=useQuery("table-data",fatchTableData)
  console.log(data,"data")

  const columns = useMemo(() => COLUMNS, []);
  const data1 = useMemo(() => MOCK_DATA, []);
  const tableInstance = useTable(
    {
      columns, // columns: columns,
      data:data1, //data: data,
    },
    usePagination
  );
  const {
    getTableBodyProps,
    getTableProps,
    headerGroups,
    page,
    nextPage,
    previousPage,
    canNextPage,
    pageOptions,
    canPreviousPage,
    prepareRow,
    state,
  } = tableInstance;
  const { pageIndex } = state;
  const options = [
    { value: "chocolate", label: "Chocolate" },
    { value: "strawberry", label: "Strawberry" },
    { value: "vanilla", label: "Vanilla" },
  ];
  return (
    <>
      <div
        style={{
          display: "flex",

          justifyContent: "space-between",
          margin: "30px 50px",
        }}
      >
        <div
          style={{
            display: "flex",
            gap: 40,
          }}
        >
          <SelectInput options={options} placeholder="select countries" />
          <SelectInput options={options} placeholder="select state" />
        </div>
        <div
          style={{
            display: "flex",
            gap: 40,
          }}
        >
          <button>submit</button>
          <button>cancel</button>
        </div>
      </div>
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((col) => (
                <th {...col.getHeaderProps()}>{col.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
      <div>
        <button onClick={() => previousPage()} disabled={!canPreviousPage}>
          {" "}
          {`<`}{" "}
        </button>
        <strong>{` page ${pageIndex + 1} of ${pageOptions.length} `}</strong>
        <button onClick={() => nextPage()} disabled={!canNextPage}>
          {" "}
          {`>`}{" "}
        </button>
      </div>
    </>
  );
};
